function foo(int $a): int { return $a; } function bar(string $b): string { return $b; } function foobar($value) { if (is_int($value)) { return foo($value); } elseif (is_string($value)) { return bar($value); } else { throw new InvalidArgumentException('Invalid argument type'); } } //This handles the type juggling and calls the appropriate function based on the type.  Explicit type checking eliminates ambiguity.